'use strict'
var MapDispatcher = require('app/dispatcher/MapDispatcher');
var MapConstants = require('app/constants/MapConstants');

var ActionTypes = MapConstants.ActionTypes;

module.exports = {

  select: function(index) {
    MapDispatcher.handleViewAction({
      type: ActionTypes.LAYER_SELECT,
      index: index
    });
  },
  
  alarm: function(indexList) {
    MapDispatcher.handleViewAction({
      type: ActionTypes.LAYER_ALARM,
      list: indexList
    });
  }

};
